void GetProcessId(const wchar_t* processName, DWORD* processId)
{
    HANDLE hProcess;
    DWORD dwProcessId;
    SYSTEM_PROCESS_INFO processInfo;

    if (!OpenProcess(PROCESS_QUERY_INFORMATION, FALSE, processId))
    {
        printf("Failed to open process with PID %d\n", processId);
        return;
    }

    if (!GetSystemInfo(&processInfo))
    {
        printf("Failed to get system info\n");
        CloseHandle(hProcess);
        return;
    }

    hProcess = OpenProcess(PROCESS_QUERY_INFORMATION | PROCESS_VM_READ, FALSE, processId);

    if (hProcess == NULL)
    {
        printf("Failed to open process with PID %d\n", processId);
        CloseHandle(hProcess);
        return;
    }

    if (!EnumProcesses(hProcess, processName, processId, NULL))
    {
        printf("Failed to get process with PID %d\n", processId);
        CloseHandle(hProcess);
        return;
    }

    dwProcessId = processId;

    printf("%s PID: %d\n", processName, dwProcessId);

    CloseHandle(hProcess);
}

