    update_filename() {
        const start_dt_str = this.start_dt.toISOString().slice(0, -7).replace(/[^0-9]/g, "");
        const end_dt_str = this.end_dt.toISOString().slice(0, -7).replace(/[^0-9]/g, "");
        this.filename = `${start_dt_str}_${end_dt_str}`;
