using namespace std;

bool is_sorted(vector<int> v) {
    int n = v.size();
    if (n < 2) {
        return true;
    }
    if (n == 2) {
        return v[0] <= v[1];
    }
    int i = 0;
    int j = n-1;
    while (i < j) {
        while (v[i] < v[j]) {
            i++;
        }
        while (v[j] > v[i]) {
            j--;
        }
        if (i < j) {
