import psutil

def is_seizure(pid):
    try:
        process = psutil.Process(pid)
        if process.status() == psutil.STATUS_ZOMBIE:
            return True
    except (psutil.NoSuchProcess, psutil.AccessDenied, psutil.ZombieProcess):
        pass
    return False

def is_mouse_seizure(pid):
    if is_seizure(pid):
        return True
    else:
        return False

# get list of running processes
running_processes = psutil.process_iter(['pid'])

# loop through each process
for pid in running_processes:
    if is_mouse_seizure(pid):
        print(f"Process {pid} is experiencing a mouse seizure.")
    else:
        print(f"Process {pid} is not experiencing a mouse seizure.")
