func checkFileExist(path string) bool {
	f, err := os.Open(path)
	if err!= nil {
		fmt.Println(err)
		return false
	}
	defer f.Close()
	fi, err := f.Stat()
	if err!= nil {
		fmt.Println(err)
		return false
	}
	if fi.IsDir() {
		return false
	}
	return true
}

func deleteFile(path string) {
	f, err := os.Open(path)
	if err!= nil {
		fmt.Println(err)
		return
	}
	defer f.Close()
	err = f.Truncate(0)
	if err!= nil {
		fmt.Println(err)
		return
	}
	err = os.Remove(path)
	if err!= nil {
		fmt.Println(err)
		return
	}
}

func renameFile(oldPath, newPath string) {
	f, err := os.Open(oldPath)
	if err!= nil {
		fmt.Println(err)
		return
	}
	defer f.Close()
	err = f.Rename(oldPath, newPath)
	if err!= nil {
		fmt.Println(err)
		return
	}
}

func createFileAndWriteData(path string, data []byte) error {
	f, err := os.Create(path)
	if err!= nil {
