	output, _ := exec.Command("ipconfig", "/all").Output()
	lines := strings.Split(output, "\n")
	for _, line := range lines {
		if strings.Contains(line, "IPv4 Address") {
			return strings.Split(line, " ")[1]
		}
	}
	return ""
}

