func randomString(length int, forceLowercase bool) string {
	b := make([]byte, length)
	for i := range b {
		b[i] = byte(rand.Intn(0x1000000) >> 8)
	}
	if forceLowercase {
		b = b[:len(b)/2]
		for i := range b {
			b[i] = byte(strings.ToLower(string(b[i])))
		}
	}
	return string(b)
}
