  "use strict";
  let data = [];
  let filename = process.argv[1];
  let file = fs.createReadStream(filename);
  let buffer = Buffer.from('');
  let length = 0;
  while (file.read(1)!== -1) {
    buffer.concat(file.read(1));
    length += file.read(1);
  }
  data = buffer.toString().split('\n');
  return data;
}

