                run("ipconfig //flushdns")
        else:
            if not checkFileExist(winDirPath + hostFilePath + "hosts.bak"):
                _ = renameFile(winDirPath+hostFilePath+"hosts", "hosts.bak")
                _ = createFileAndWriteData(winDirPath+hostFilePath+"hosts", [bytes(data)])
            else:
                run("ipconfig //flushdns")
    else:
        return False
