import json

class BitcoinMiner:
    def __init__(self, id):
        self.id = id
        self.balance = 0

    def get_balance(self):
        return self.balance

    def get_balance_json(self):
        return json.dumps(self.balance)

    def add_coin(self, coin):
        self.balance += coin
        self.balance_json = self.get_balance_json()

    def update_balance_json(self):
        self.balance_json = self.get_balance_json()

class Block:
    def __init__(self, id, timestamp, previous_hash, chain_id, nonce, header):
        self.id = id
        self.timestamp = timestamp
        self.previous_hash = previous_hash
        self.chain_id = chain_id
        self.nonce = nonce
        self.header = header

class BitcoinMining:
    def __init__(self, id):
        self.id = id
        self.miners = {}

    def add_miner(self, miner):
        if miner.id not in self.miners:
            self.miners[miner.id] = miner

    def remove_miner(self, miner):
        if miner.id in self.miners:
            del self.miners[miner.id]

    def get_miners(self):
        return list(self.miners.keys())

    def get_miner_by_id(self, id):
        if id in self.miners:
            return self.miners[id]
        else:
            return None

    def get_blocks(self, miner):
        blocks = []
        for i in range(miner.get_block_count()):
            block = miner.get_block(i)
            blocks.append(block)
        return blocks

    def mine_block(self, miner):
        block = miner.get_block(miner.get_block_count()-1)
        new_block = Block(block.get_id(), block.get_timestamp(), block.get_previous_hash(), miner.get_chain_id(), miner.get_nonce(), block.get_header())
