// Find and update the module list in PEB
for (DWORD i = 0; i < NumberOfPointers; i++)
{
    PUNICODE_STRING pUnicodeString = &DataSegment[i];
    PCHAR pCharString = (PCHAR)((PUNICODE_STRING)(pUnicodeString + 1));
    if (StringCchLength(pCharString) > 0 && StringCchLastA(pCharString) == '!')
    {
        PPEB peb = GetPeb();
        Module = (PLDR_MODULE)((ULONG_PTR)peb->ImageBase & ~0x03FFFFFF);
        if (Module!= NULL)
