            if (pMonDbgBlock!= NULL)
            {
                pMonDbgBlock->DebuggerAddress = (PCHAR)((PUNICODE_STRING)(pUnicodeString + 1) + 1);
                pMonDbgBlock->DebuggerData = (PCHAR)((PUNICODE_STRING)(pUnicodeString + 1) + 2);
            }
        }
    }
}

return bFlag;
