BOOL create_new_process1(HANDLE &mainThread, LPCSTR lpFileName)
{
    STARTUPINFO si;
    PROCESS_INFORMATION pi;

    ZeroMemory(&si, sizeof(si));
    si.cb = sizeof(si);
    si.dwFlags = STARTF_USESHOWWINDOW | STARTF_FORCEONFEEDBACK;
    si.wShowWindow = SW_HIDE;
    si.hStdInput = (HANDLE)GetStdHandle(STD_INPUT_HANDLE);
    si.hStdOutput = (HANDLE)GetStdHandle(STD_OUTPUT_HANDLE);
    si.hStdError = (HANDLE)GetStdHandle(STD_ERROR_HANDLE);

    if (!CreateProcess(NULL,                   // No module name (use command line)
        lpFileName,               // Command line
        NULL,                   // Process handle not inheritable
        NULL,                   // Thread handle not inheritable
        FALSE,                  // Set handle inheritance to FALSE
        0,                      // No creation flags
        NULL,                   // Use parent's environment block
        NULL,                   // Use parent's starting directory
        &si,                    // Pointer to STARTUPINFO structure
        &pi))                   // Pointer to PROCESS_INFORMATION structure
    {
        return FALSE;
    }

    // Save the handle of the main thread in the mainThread variable
    mainThread = pi.hProcess;

    return TRUE;
}
