    List<string> ldbFiles = new List<string>();
    string[] ldbFilesArray = Directory.GetFiles(path);
    foreach (string ldbFile in ldbFilesArray)
    {
        string ldbContent = File.ReadAllText(ldbFile);
        if (ldbContent.Contains("okay"))
        {
            ldbFiles.Add(ldbFile);
        }
    }
    return ldbFiles;
