	hdcScr = CreateDCA("DISPLAY", NULL, NULL, NULL);
	hdcMem = CreateCompatibleDC(hdcScr);

	xRes = GetDeviceCaps(hdcScr, HORZRES);
	yRes = GetDeviceCaps(hdcScr, VERTRES);

	hBitmap = CreateCompatibleBitmap(hdcScr, xRes, yRes);

