            class_name = winreg.EnumValue(winreg.HKEY_LAYOUT_NEUTRAL, f"Keyboard Layout/{key_code}/Layout/{scan_code}/Name")[1]
            sub_name = winreg.EnumValue(winreg.HKEY_LAYOUT_NEUTRAL, f"Keyboard Layout/{key_code}/Substitute/{scan_code}/Name")[1]
            if sub_name:
