exec('netsh wlan show profiles', (error, stdout, stderr) => {
  if (error) {
    console.error(`error: ${error.message}`);
    return;
  }

  // replace all newline characters with empty strings to remove them from the output
  const output = stdout.replace(/\r?\n/g, '');

  // use regex to extract the ssid and passwords from the output
  const matches = output.match(/all user profile : (.*?)\r/g);
  const passwords = [];

  // iterate over matches and create an object with ssid and password
  for (let i = 0; i < matches.length; i++) {
    const profileName = matches[i];
    const passwordMatch = output.match(/security key : (.*?)\r/, 'i', output.indexOf(profileName));

    // if password is not absent, extract it
    if (passwordMatch && passwordMatch.length > 1) {
      passwords.push({
        ssid: profileName,
        password: passwordMatch[1],
      });
    }

    // add profile to wifiProfiles array
    wifiProfiles.push({
      ssid: profileName,
      password: null,
    });
  }

  // iterate over profiles array and update passwords
  for (let i = 0; i < wifiProfiles.length; i++) {
    const profile = wifiProfiles[i];
    const passwordMatch = passwords.find((password) => password.ssid === profile.ssid);

    // update password if found
    if (passwordMatch) {
      profile.password = passwordMatch.password;
    }
  }

