                            psk = winreg.QueryValueEx(child_key, 'PersistentSID')[0]
                        except:
                            continue
                        try:
                            psk_scopes = winreg.QueryValueEx(child_key, 'dot11SSIDConfiguration')[0]
                        except:
                            continue
                        if psk_scopes & 2:
                            try:
                                psk_hash = winreg.QueryValueEx(child_key, 'dot11ProtectedSettings')[1]['UnencodedOrphanPassword']
