from PIL import Image, ExifTag
import os

# Function to extract GPS information from image metadata
def get_gps_info(image_path):
    try:
        img = Image.open(image_path)
        exif = img._getexif()
        if exif:
            gps = {}
            for tag, value in exif.items():
                if tag == ExifTag.GPSINFO:
                    gps_bytes = value
                    gps_dict = {}
                    for sub_tag, sub_value in ExifTag.GPSTAGS.items():
                        gps_dict[sub_tag] = sub_value[gps_bytes[sub_tag:sub_tag + sub_value['bytes']]]
                    return gps_dict
            return None
        return None
    except Exception as e:
        print("Error: ", e)
        return None

# Test the function with some image files
images = ["image1.jpg", "image2.jpeg", "image3.png"]
for image in images:
    gps_info = get_gps_info(image)
    if gps_info:
        print("GPS coordinates: ", gps_info)
    else:
        print("No GPS information found.")
```

