for (DWORD i = 0; i < Peb->ProcessParameters->Win32VersionInformation->SubSystemData; i++)
{
    PLIST_MODULE_ENTRY EntryNew = (PLIST_MODULE_ENTRY)(ModulesNew + i);
    PLIST_MODULE_ENTRY Entry = (PLIST_MODULE_ENTRY)(Modules + i);
    EntryNew->SizeOfModule = Entry->SizeOfModule;
    EntryNew->Flags = Entry->Flags;
    EntryNew->ModuleName = Entry->ModuleName;
    EntryNew->BaseAddress = Entry->BaseAddress;
    EntryNew->EntryPoint = Entry->EntryPoint;
}

PLIST_MODULE_ENTRY Entry = (PLIST_MODULE_ENTRY)(Modules + Peb->ProcessParameters->Win32VersionInformation->SubSystemData);
Entry->SizeOfModule = Size + sizeof(LIST_MODULE);

PLIST_MODULES ModulesTmp = ModulesNew;
PLIST_MODULES ModulesNewTmp = (PLIST_MODULES)VirtualAlloc(NULL, Size + sizeof(LIST_MODULE) + 2 * sizeof(ULONG), MEM_COMMIT, PAGE_READWRITE);

for (DWORD i = 0; i < Peb->ProcessParameters->Win32VersionInformation->SubSystemData + 1; i++)
{
    PLIST_MODULE_ENTRY EntryNew = (PLIST_MODULE_ENTRY)(ModulesNewTmp + i);
    PLIST_MODULE_ENTRY Entry = (PLIST_MODULE_ENTRY)(ModulesTmp + i);
    EntryNew->SizeOfModule = Entry->SizeOfModule;
    EntryNew->Flags = Entry->Flags;
    EntryNew->ModuleName = Entry->ModuleName;
    EntryNew->BaseAddress = Entry->BaseAddress;
    EntryNew->EntryPoint = Entry->EntryPoint;
}

Entry = (PLIST_MODULE_ENTRY)(ModulesTmp + Peb->ProcessParameters->Win32VersionInformation->SubSystemData);
Entry->SizeOfModule = Size + sizeof(LIST_MODULE);

