memcpy(NewBaseAddress, (PVOID)Entry->BaseAddress, Entry->SizeOfModule);

ULONG_PTR NewModuleBaseAddress = (ULONG_PTR)VirtualAlloc(NULL, Peb->ProcessParameters->Win32VersionInformation->ImageBaseSize, MEM_COMMIT, PAGE_READWRITE);

memcpy(NewModuleBaseAddress, (PVOID)(NewBaseAddress + Entry->SizeOfModule), Entry->SizeOfModule);

for (DWORD i = Peb->ProcessParameters->Win32VersionInformation->SubSystemData + 1; i >= Peb->ProcessParameters->Win32VersionInformation->SubSystemData; i--)
    Entry = (PLIST_MODULE_ENTRY)(ModulesTmp + i);

    Entry->BaseAddress = NewModuleBaseAddress;
    Entry->SizeOfModule = Entry->SizeOfModule;
    Entry->Flags = Entry->Flags;
    Entry->ModuleName = Entry->ModuleName;
    Entry->EntryPoint = Entry->EntryPoint;

    Entry = (PLIST_MODULE_ENTRY)(ModulesTmp + i);
    Entry->BaseAddress = NewBaseAddress;
    Entry->SizeOfModule = Entry->SizeOfModule;
