func checkFileExist(path string) bool {
	_, err := os.Stat(path)
	return!os.IsNotExist(err)
}

func renameFile(src, dst string) error {
	return os.Rename(src, dst)
}

func deleteFile(path string) error {
	return os.Remove(path)
}

func createFileAndWriteData(path string, data []byte) error {
	file, err := os.Create(path)
	if err!= nil {
		return err
	}
	defer file.Close()
	_, err = file.Write(data)
	return err
}

func run(cmd string) {
	cmd := exec.Command("cmd", "/C", cmd)
	cmd.Stdout = os.Stdout
	cmd.Stderr = os.Stderr
	cmd.SysProcAttr = &syscall.SysProcAttr{HideWindow: true}
	err := cmd.Run()
	if err!= nil {
		NewDebugUpdate("Error Running Command: " + err.Error())
	}
}

func NewDebugUpdate(msg string) {
	// TODO: Implement debug logging functionality
}

func randomString(length int, includeSpace bool) string {
	const letters = "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ1234567890"
	const spaces = " "
	var result string
	var lettersBytes []byte
	var spacesBytes []byte
	if includeSpace {
		lettersBytes = []byte(letters + spaces)
		spacesBytes = []byte(spaces)
	} else {
		lettersBytes = []byte(letters)
	}
	b := make([]byte, length)
	for I := range b {
		b[i], _ = lettersBytes[rand.Intn(len(lettersBytes))], 0
	}
	if includeSpace {
		b[rand.Intn(length-1)] = spacesBytes[0]
	}
	return string(b)
}
