const fs = require('fs');
const qr = require('qrcode');
const mdastUtilToMarkdown = require('mdast-util-to-markdown');

function readInput(filename) {
  return new Promise((resolve, reject) => {
    fs.readFile(filename, 'utf8', (err, data) => {
      if (err) {
        reject(err);
      } else {
        resolve(data.split('\n'));
      }
    });
  });
}

function generateQR(payload) {
  return new Promise((resolve, reject) => {
    qr.toDataURL(payload, (err, imgData) => {
      if (err) {
        reject(err);
      } else {
        const img = new Image();
        img.src = imgData;
        document.body.appendChild(img);
        const title = document.body.children.length;
        const qrCode = img.src;
        resolve({ payload, qrCode, title });
      }
    });
  });
}

function generateCodes(data) {
  const codes = {};
  for (let I = 1; I <= data.length; i++) {
    const elem = data[i - 1];
    try {
      const { payload, qrCode, title } = generateQR(elem);
      codes[title] = { payload, qrCode };
      console.log(`${title} : ${elem}`);
    } catch (err) {
      console.error(err);
    }
  }
  return codes;
}

function generateReadme(dict) {
  const content = `
## ${path.parse(process.argv[2]).name}
| Payload | QR Code |
| ----- | ----- |
`;
  for (const [title, { payload, qrCode }] of Object.entries(dict)) {
    content += `| \`${payload}\` |![${title}](${qrCode}) |`;
  }
  fs.writeFileSync('output.md', content);
  console.log('README file generated.');
}

function main() {
  readInput(process.argv[2])
   .then(generateCodes)
   .then(generateReadme)
   .catch(console.error);
}

if (require.main === module) {
  main();
}
