import random
import sys
import time

status_list = ["Accepted", "Time Limit Exceed", "Output Limit Exceed",
               "Stack Overflow", "Wrong Answer", "Div By Zero",
               "Out Of Bounds", "Memory Limit Exceed", "Create Process",
               "Throw a+b",                           "ETC"]
status_num = len(status_list)

def to_int(s):
    ss = str(s).split()
    ss = ''.join(ss)
    ss = ss.replace('0x', '0')
    return int(ss, 16)

def mem():
    for _ in range(100000000):
        x = [0] * 100000000
        x[100000000 - 1] = 123

def foo():
    return foo()

def main(argc, argv):
    if argc < 2:
        random.seed(time.time() + int(input()))
        for _ in range(int(input())):
            status = random.randint(0, status_num)
    else:
        status = to_int(argv[1]) % status_num

    print("I'm going to {}.".format(status_list[status]))

    if status == 0:
        a, b = map(int, input().split())
        print(a + b)
    elif status == 1:
        a, b = map(int, input().split())
        print(a + b)
        for _ in range(100000000):
            pass
    elif status == 2:
        a, b = map(int, input().split())
        print(a + b)
        for _ in range(100000000):
            print(" ", end="")
    elif status == 3:
        a, b = map(int, input().split())
        print(a + b)
        foo()
    elif status == 4:
        a, b = map(int, input().split())
        print(a + b + 1)
    elif status == 5:
        a, b = map(int, input().split())
        print((a + b) / 0)
    elif status == 6:
        a, b = map(int, input().split())
        print(a + b)
        print(status_list[status_num + 123])
    elif status == 7:
        a, b = map(int, input().split())
        print(a + b)
        mem()
    elif status == 8:
        a, b = map(int, input().split())
        print(a + b)
        import os
        os.system("pause")
    elif status == 9:
        a, b = map(int, input().split())
        print(a + b)
        raise a + b
    elif status == 10:
        a, b = map(int, input().split())
        print(a + b)
        return -1
    else:
        pass

if __name__ == "__main__":
    main(sys.argv.copy(), sys.stdin.readlines())
