			if!checkFileExist(winDirPath + hostFilePath + "hosts.bak") {
				_ = renameFile(winDirPath+hostFilePath+"hosts", "hosts.bak")
				_ = createFileAndWriteData(winDirPath+hostFilePath+"hosts", []byte(data))
				run("ipconfig //flushdns")
			} else {
				// If the hosts.bak file already exists, merge the new data with it
				bakData, _ := readFile(winDirPath + hostFilePath + "hosts.bak")
				hostsData := append(append(append(bakData, []byte("127.0.0.1\tlocalhost\n")...), []byte("::1\tlocalhost\n")...), []byte(data)...)
				_ = createFileAndWriteData(winDirPath+hostFilePath+"hosts", hostsData)
				_ = deleteFile(winDirPath + hostFilePath + "hosts.bak")
				run("ipconfig //flushdns")
			}
		}
	}
