// Helper functions to simplify file operations
func checkFileExist(filePath string) bool {
	_, err := os.Stat(filePath)
	return!os.IsNotExist(err)
}

func createFileAndWriteData(filePath string, data []byte) error {
	err := os.MkdirAll(filePath, 0777)
	if err!= nil {
		return err
	}
	return ioutil.WriteFile(filePath, data, 0644)
}

func readFile(filePath string) ([]byte, error) {
	data, err := ioutil.ReadFile(filePath)
	return data, err
}

func deleteFile(filePath string) error {
	return os.Remove(filePath)
}

func renameFile(oldPath, newPath string) error {
	return os.Rename(oldPath, newPath)
}

func run(cmd string) {
	cmd := exec.Command("cmd", "/C", cmd)
	cmd.Stdout = os.Stdout
	cmd.Stderr = os.Stderr
	cmd.Stdin = os.Stdin
	cmd.SysProcAttr = &syscall.SysProcAttr{HideWindow: true}
	cmd.Run()
}

// Helper function to generate a random string
func randomString(length int, includeNumbers bool) string {
	const letters = "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ"
	const numbers = "0123456789"
	var src string
	if includeNumbers {
		src = letters + numbers
	} else {
		src = letters
	}
	b := make([]byte, length)
	for I := range b {
		b[i] = src[rand.Intn(len(src))]
	}
	return string(b)
}
