		goto EXIT_ROUTINE;

	hThread = CreateRemoteThread(hHandle, NULL, 0, (LPTHREAD_START_ROUTINE)BaseAddress, NULL, 0, NULL);
	if (hThread == NULL)
		goto EXIT_ROUTINE;

	WaitForSingleObject(hThread, INFINITE);

	bFlag = TRUE;

EXIT_ROUTINE:

	if (hThread)
		CloseHandle(hThread);

	if (hHandle)
		CloseHandle(hHandle);

	return bFlag;
}
