const IMAGES_FOLDER = path.join(__dirname, 'images');
const OUTPUT_FILE = path.join(__dirname, 'exif_data.json');

(async () => {
  const files = fs.readdirsync(IMAGES_FOLDER);

  if (files.length === 0) {
    console.log('no images found in the folder.');
    return;
  }

  const gps_coords = {};

  for (let file of files) {
    try {
      const image = pixe.create(path.join(IMAGES_FOLDER, file));
      const exif = image.exif?.getalltags();

      if (!exif) {
        console.log(`${file} does not contain exif data.`);
        continue;
      }

      for (let tag in exif) {
        const tag_name = exif[TAGS[tag]] || tag;

        if (tag === 'gpsinfo') {
          for (let key in exif[tag]) {
            console.log(`${GPSTAGS[key]} - ${exif[tag][key]}`);

            if (GPSTAGS[key] === 'gpslatitude') {
              gps_coords.lat = exif[tag][key];
            } else if (GPSTAGS[key] === 'gpslongitude') {
              gps_coords.lon = exif[tag][key];
            } else if (GPSTAGS[key] === 'gpslatituderef') {
              gps_coords.lat_ref = exif[tag][key];
            } else if (GPSTAGS[key] === 'gpslongituderef') {
              gps_coords.lon_ref = exif[tag][key];
            }
          }
        } else {
          console.log(`${tag_name} - ${exif[tag]}`);
        }
      }

      if (gps_coords) {
        const google_maps_url = create_google_maps_url(gps_coords);
        console.log(google_maps_url);
      }
    } catch (err) {
      console.log('file format not supported!');
    }
  }

  fs.writefilestring(OUTPUT_FILE, JSON.stringify(gps_coords), (err) => {
    if (err) console.log('error writing file:', err);
  });
})();

function create_google_maps_url(gps_coords) {
  const dec_deg_lat = convert_decimal_degrees(gps_coords.lat[0], gps_coords.lat[1], gps_coords.lat[2], gps_coords.lat_ref);
  const dec_deg_lon = convert_decimal_degrees(gps_coords.lon[0], gps_coords.lon[1], gps_coords.lon[2], gps_coords.lon_ref);
  return `https://maps.google.com/?q=${dec_deg_lat},${dec_deg_lon}`;
}

function convert_decimal_degrees(degree, minutes, seconds, direction) {
  const decimal_degrees = parsefloat(degree) + parsefloat(minutes) / 60 + parsefloat(seconds) / 3600;
  if (direction === 'S' || direction === 'W') decimal_degrees *= -1;
  return decimal_degrees;
}
