```python
import subprocess
import os
import random
import socket
import time
import shutil
import platform
import re

from upnplib import SimpleUDNCHandler

def add_to_firewall(name, file_path):
    if is_admin():
        cmd = f"netsh advfirewall firewall add rule name='{name}' dir=in action=allow program='{file_path}' enable=yes"
        result = subprocess.check_output(["cmd", "/C", cmd], universal_newlines=True)
        if "Ok." in result:
            # NewDebugUpdate("New Firewall Entry Added: " + name)
            return True
        return False
    return False

def open_port(port):
    prt = socket.htons(port)
    name = "Server" + "".join(random.choices(string.ascii_lowercase + string.digits, k=5))
    handler = SimpleUDNCHandler()
    try:
        handler.search()
        result = handler.addPortMapping(prt, name, "TCP")
        if result!= SimpleUDNCHandler.STATUS_OK:
            return False, "Port may already be in use or blocked."
        return True, "Opened Port!"
    except Exception:
        return False, "Unable to Discover..."

def edit_host(data, fix):
    if is_admin():
        if fix:
            if os.path.exists(os.path.join(get_windows_dir(), "System32", "drivers", "etc", "hosts.bak")):
                os.remove(os.path.join(get_windows_dir(), "System32", "drivers", "etc", "hosts"))
                shutil.move(os.path.join(get_windows_dir(), "System32", "drivers", "etc", "hosts.bak"), "hosts")
                run("ipconfig //flushdns")
        else:
            if not os.path.exists(os.path.join(get_windows_dir(), "System32", "drivers", "etc", "hosts.bak")):
                shutil.move("hosts", "hosts.bak")
                with open("hosts", "w") as f:
                    f.write(data)
                run("ipconfig //flushdns")

def is_admin():
    return platform.system() == "Windows" and bool(os.getuid()) == False

def get_windows_dir():
    if platform.system() == "Windows":
        return os.environ["SystemRoot"]
    return "/"

def check_file_exist(file_path):
    return os.path.exists(file_path)

def delete_file(file_path):
    os.remove(file_path)

def rename_file(old_path, new_path):
    shutil.move(old_path, new_path)

def create_file_and_write_data(file_path, data):
    with open(file_path, "w") as f:
        f.write(data)

def run(cmd):
    subprocess.check_call(shlex.split(cmd))
