import ctypes
import time
import math

def move_window(hwnd, x, y, width, height):
    ctypes.windll.user32.MoveWindow.restype = ctypes.windll.user32.MoveWindow.argtypes = [ctypes.c_hwnd, ctypes.c_int, ctypes.c_int, ctypes.c_int, ctypes.c_int, ctypes.c_int]
    ctypes.windll.user32.MoveWindow(hwnd, x, y, width, height)

def moveit(hwnd):
    for k in range(1, 101):
        x = 200 + 300 * math.cos(k)
        y = 150 + 300 * math.sin(k)
        move_window(hwnd, int(x), int(y), int(x), int(y))
        time.sleep(0.05)

def get_active_window():
    return ctypes.windll.user32.GetForegroundWindow()

def hide_window(hwnd):
    ctypes.windll.user32.ShowWindow.restype = ctypes.windll.user32.ShowWindow.argtypes = [ctypes.c_hwnd, ctypes.c_int]
    ctypes.windll.user32.ShowWindow(hwnd, ctypes.c_int(32))

